from functools import partial
import jax.numpy as jnp from jax import jit
from wblbm.grid import Grid from wblbm.operators.macroscopic.macroscopic import Macroscopic from wblbm.operators.differential.gradient import Gradient from wblbm.operators.differential.laplacian import Laplacian from wblbm.lattice.lattice import Lattice
class MacroscopicMultiphaseCS(Macroscopic): """ Calculates macroscopic variables for multiphase simulations. Inherits from Macroscopic and adds multiphase-specific methods. This is the implementation of the Carnahan Starling equation of state. """
def __init__( self, grid: Grid, lattice: Lattice, kappa: float, interface_width: int, rho_l: float, rho_v: float, a_eos: float, b_eos: float, R_eos: float, T_eos: float, force_enabled: bool = False, ): super().__init__( grid, lattice, force_enabled=force_enabled ) self.kappa = kappa self.rho_l = rho_l self.rho_v = rho_v self.gradient = Gradient(lattice) self.laplacian = Laplacian(lattice) self.beta = 8 * kappa / (float(interface_width) ** 2 * (rho_l - rho_v) ** 2) self.a_eos = a_eos self.b_eos = b_eos self.R_eos = R_eos self.T_eos = T_eos
@partial(jit, static_argnums=(0,)) def __call__( self, f: jnp.ndarray, force: jnp.ndarray = None ) -> tuple[jnp.ndarray, jnp.ndarray, jnp.ndarray]: """ Calculate the macroscopic density and velocity fields from the population distribution.
Args: f (jnp.ndarray): Population distribution, shape (nx, ny, q, 1) force (jnp.ndarray, optional): External force field, shape (nx, ny, 1, 2)
Returns: tuple: (rho, u_eq, force_total) rho (jnp.ndarray): Density field, shape (nx, ny, 1, 1) u_eq (jnp.ndarray): Force-corrected velocity for equilibrium, shape (nx, ny, 1, 2) force_total (jnp.ndarray): Total force (interaction + external), shape (nx, ny, 1, 2) """ rho, u = super().__call__(f, force=None) # Pass None to avoid any correction
# Calculate interaction force force_int = self.force_int(rho) # Total force calculation if force is not None: force_total = force + force_int else: force_total = force_int
u_eq = u + force_total / (2 * rho) # divide by rho for proper correction
return rho, u_eq, force_total
@partial(jit, static_argnums=(0,)) def carnahan_starling_eos(self, rho): """Equation of state - extract 2D data for computation""" rho_2d = rho[:, :, 0, 0] # Extract (nx, ny) from (nx, ny, 1, 1) eos_2d = ( -(2*self.a_eos)*rho_2d + self.R_eos*self.T_eos*(1 + jnp.log(rho_2d)) )
# Convert back to 4D format eos_4d = jnp.zeros_like(rho) eos_4d = eos_4d.at[:, :, 0, 0].set(eos_2d) return eos_4d
@partial(jit, static_argnums=(0,)) def chem_pot(self, rho): """ Calculate the chemical potential. """ mu_0 = self.carnahan_starling_eos(rho) chem_pot__ = mu_0 - self.kappa * self.laplacian(rho) return chem_pot__
@partial(jit, static_argnums=(0,)) def force_int(self, rho): """ Calculate the interaction force. """ grad_chem_pot = self.gradient(self.chem_pot(rho)) # Shape: (nx, ny, 1, 2) # Return -rho * grad_chem_pot, shape (nx, ny, 1, 2) return -rho * grad_chem_pot
@partial(jit, static_argnums=(0,)) def u_new(self, u, force): """ Update velocity with interaction force. """ # Both u and force have shape (nx, ny, 1, 2) return u + force / 2

from functools import partial
import jax.numpy as jnp from jax import jit
from wblbm.grid import Grid from wblbm.operators.macroscopic.macroscopic import Macroscopic from wblbm.operators.differential.gradient import Gradient from wblbm.operators.differential.laplacian import Laplacian from wblbm.lattice.lattice import Lattice
class MacroscopicMultiphaseCS(Macroscopic): """ Calculates macroscopic variables for multiphase simulations. Inherits from Macroscopic and adds multiphase-specific methods. This is the implementation of the Carnahan Starling equation of state. """
def __init__( self, grid: Grid, lattice: Lattice, kappa: float, interface_width: int, rho_l: float, rho_v: float, a_eos: float, b_eos: float, R_eos: float, T_eos: float, force_enabled: bool = False, ): super().__init__( grid, lattice, force_enabled=force_enabled ) self.kappa = kappa self.rho_l = rho_l self.rho_v = rho_v self.gradient = Gradient(lattice) self.laplacian = Laplacian(lattice) self.beta = 8 * kappa / (float(interface_width) ** 2 * (rho_l - rho_v) ** 2) self.a_eos = a_eos self.b_eos = b_eos self.R_eos = R_eos self.T_eos = T_eos
@partial(jit, static_argnums=(0,)) def __call__( self, f: jnp.ndarray, force: jnp.ndarray = None ) -> tuple[jnp.ndarray, jnp.ndarray, jnp.ndarray]: """ Calculate the macroscopic density and velocity fields from the population distribution.
Args: f (jnp.ndarray): Population distribution, shape (nx, ny, q, 1) force (jnp.ndarray, optional): External force field, shape (nx, ny, 1, 2)
Returns: tuple: (rho, u_eq, force_total) rho (jnp.ndarray): Density field, shape (nx, ny, 1, 1) u_eq (jnp.ndarray): Force-corrected velocity for equilibrium, shape (nx, ny, 1, 2) force_total (jnp.ndarray): Total force (interaction + external), shape (nx, ny, 1, 2) """ rho, u = super().__call__(f, force=None) # Pass None to avoid any correction
# Calculate interaction force force_int = self.force_int(rho) # Total force calculation if force is not None: force_total = force + force_int else: force_total = force_int
u_eq = u + force_total / (2 * rho) # divide by rho for proper correction
return rho, u_eq, force_total
@partial(jit, static_argnums=(0,)) def carnahan_starling_eos(self, rho): """Equation of state - extract 2D data for computation""" rho_2d = rho[:, :, 0, 0] # Extract (nx, ny) from (nx, ny, 1, 1) # Julia formula: # -(2*a_eos)*ﾏ『[i,j] + R_eos*T_eos*(1 + log(ﾏ『[i,j])) + ((16*R_eos*T_eos*(b_eos*ﾏ『[i,j] -12))/((-4 + b_eos*ﾏ『[i,j])^3)) eos_2d = ( -(2 * self.a_eos) * rho_2d + self.R_eos * self.T_eos * (1 + jnp.log(rho_2d)) + (16 * self.R_eos * self.T_eos * (self.b_eos * rho_2d - 12)) / jnp.power((-4 + self.b_eos * rho_2d), 3) )
# Convert back to 4D format eos_4d = jnp.zeros_like(rho) eos_4d = eos_4d.at[:, :, 0, 0].set(eos_2d) return eos_4d
@partial(jit, static_argnums=(0,)) def chem_pot(self, rho): """ Calculate the chemical potential. """ mu_0 = self.carnahan_starling_eos(rho) chem_pot__ = mu_0 - self.kappa * self.laplacian(rho) return chem_pot__
@partial(jit, static_argnums=(0,)) def force_int(self, rho): """ Calculate the interaction force. """ grad_chem_pot = self.gradient(self.chem_pot(rho)) # Shape: (nx, ny, 1, 2) # Return -rho * grad_chem_pot, shape (nx, ny, 1, 2) return -rho * grad_chem_pot
@partial(jit, static_argnums=(0,)) def u_new(self, u, force): """ Update velocity with interaction force. """ # Both u and force have shape (nx, ny, 1, 2) return u + force / 2

import numpy as np
import jax.numpy as jnp
import inspect  # Added for simulation name detection


class SimulationFactory:
    @staticmethod
    def create_simulation(simulation_type, **kwargs):
        if simulation_type == "singlephase":
            from wblbm.simulations.singlephase import SinglePhaseSimulation
            return SinglePhaseSimulation(**kwargs)
        elif simulation_type == "multiphase":
            from wblbm.simulations.multiphase import MultiphaseSimulation
            return MultiphaseSimulation(**kwargs)
        else:
            raise ValueError(f"Unknown simulation type: {simulation_type}")


class Run:
    """
    Main class to configure and run lattice Boltzmann simulations using the simulation factory.
    """

    def __init__(
        self,
        simulation_type="singlephase",
        *,
        save_interval=100,
        results_dir="results",
        init_type="standard",
        init_dir=None,
        skip_interval=0,
        collision=None,  # Accept collision as a kwarg
        simulation_name=None,  # Added simulation_name parameter
        **kwargs,
    ):
        # Accept either a string or a dict for collision
        collision_cfg = None
        if collision is not None:
            if isinstance(collision, str):
                collision_cfg = {"collision_scheme": collision}
            elif isinstance(collision, dict):
                collision_cfg = collision.copy()
            else:
                raise ValueError(
                    "collision must be either a string (for BGK) or dict (for MRT config)."
                )
            kwargs.update(collision_cfg)
        self.simulation = SimulationFactory.create_simulation(simulation_type, **kwargs)
        self.save_interval = save_interval
        self.skip_interval = skip_interval
        self.results_dir = results_dir
        self.init_type = init_type
        self.init_dir = init_dir
        # Auto-detect simulation name from calling function if not provided
        if simulation_name is None:
            frame = inspect.currentframe()
            try:
                caller_frame = frame.f_back
                while caller_frame:
                    func_name = caller_frame.f_code.co_name
                    if func_name != "<module>" and not func_name.startswith("_"):
                        simulation_name = func_name
                        break
                    caller_frame = caller_frame.f_back
            finally:
                del frame
        self.config = self._build_config(
            simulation_type=simulation_type,
            save_interval=save_interval,
            results_dir=results_dir,
            skip_interval=skip_interval,
            init_type=init_type,
            init_dir=init_dir,
            **kwargs,
        )
        from wblbm.utils.io import SimulationIO

        self.io_handler = SimulationIO(
            base_dir=results_dir, config=self.config, simulation_name=simulation_name
        )

    def _build_config(self, **kwargs):
        # Simple config builder for demonstration; extend as needed
        return dict(**kwargs)

    # TODO: need to double check that data is saved properly
    def _save_data(self, it, fprev):
        # Save data using the simulation's macroscopic operator
        force_ext = None
        if hasattr(self.simulation, "macroscopic"):
            macroscopic = self.simulation.macroscopic
            try:
                if self.config.get("force_enabled") and self.config.get("force_obj"):
                    rho = jnp.sum(fprev, axis=2, keepdims=True)
                    force = self.config.get("force_obj")
                    if self.config.get("simulation_type") == "multiphase":
                        force_ext = force.compute_force(
                            rho, self.config.get("rho_l"), self.config.get("rho_v")
                        )
                    else:
                        force_ext = force.compute_force(rho)
                    result = macroscopic(fprev, force_ext)
                else:
                    result = macroscopic(fprev)
                if isinstance(result, tuple) and len(result) == 3:
                    rho, u, force = result
                    data_to_save = {
                        "rho": np.array(rho),
                        "u": np.array(u),
                        "force": np.array(force),
                        "force_ext": np.array(force_ext),
                        "f": np.array(fprev),
                    }
                else:
                    rho, u = result
                    data_to_save = {
                        "rho": np.array(rho),
                        "u": np.array(u),
                        "f": np.array(fprev),
                    }
            except Exception:
                data_to_save = {"f": np.array(fprev)}
        else:
            data_to_save = {"f": np.array(fprev)}
        self.io_handler.save_data_step(it, data_to_save)

    def run(self, *, verbose=True):
        fprev = self.simulation.initialize_fields(
            self.init_type, init_dir=self.init_dir
        )
        nt = getattr(self.simulation, "nt", 1000)
        if verbose:
            print(f"Starting LBM simulation with {nt} time steps...")
            print(
                f"Config -> Grid: {self.simulation.grid_shape}, Multiphase: {self.simulation.multiphase}, Wetting: {self.simulation.wetting_enabled}, Force: {self.simulation.force_enabled}"
            )
        for it in range(nt):
            fprev = self.simulation.run_timestep(fprev, it)
            if jnp.isnan(fprev).any():
                print(f"NaN encountered at timestep {it}. Stopping simulation.")
                break
            # skip initial transients then save every `save_interval`
            if (it > self.skip_interval) and (
                it % self.save_interval == 0 or it == nt - 1
            ):
                self._save_data(it, fprev)
                if verbose and hasattr(self.simulation, "macroscopic"):
                    result = self.simulation.macroscopic(fprev)
                    if isinstance(result, tuple) and len(result) >= 2:
                        rho, u = result[:2]
                        avg_rho = np.mean(rho)
                        max_u = np.max(np.sqrt(u[..., 0] ** 2 + u[..., 1] ** 2))
                        print(
                            f"Step {it}/{nt}: avg_rho={avg_rho:.4f}, max_u={max_u:.6f}"
                        )
        if verbose:
            print("Simulation completed!")
            print(f"Results saved in: {self.io_handler.run_dir}")

import numpy as np
import jax.numpy as jnp
import inspect  # Added for simulation name detection


